import React, { Component } from 'react'
import styled from 'styled-components'

const TextSectionWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 25vw
`
const Name = styled.div`
  font-family: 'Major Mono Display', monospace;
  font-size: 48px;
  text-transform: lowercase;
`

const AboutMe = styled.p`
  font-family: 'Inconsolata', monospace;
  font-size: 14px;  
  font-family: Inconsolata;
  font-style: normal;
  font-weight: normal;
  line-height: normal; 
`

class TextSection extends Component {
  render () {
    return (
      <TextSectionWrapper>
        <Name>Ariel Lam</Name>
        <AboutMe>
          Hi! I'm Ariel, and I'm a 2B Software Engineering student at the University of Waterloo.
          <br/>
          I'm passionate about mobile and front-end development, design, and user experiences.
          Previously at Tribalscale, <a href="https://www.bunch.live/">Bunch</a> and RBC.
          I'm currently looking for opportunities for Fall 2019.
        </AboutMe>
      </TextSectionWrapper>
    )
  }
}

export default TextSection
