import React, { Component } from 'react'
import styled from 'styled-components'

const ExpCellContainer = styled.div`
  margin: 20px;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  @media (max-width: 768px) {
    flex-direction: column;
    justify-content: center;
  }
`

const Header = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
`;

const Company = styled.div`
  color: black;
  font-family: "Work Sans", monospace;
  font-size: 18pt;
  font-weight: 600;
`

const TechStackTitle = styled.div`
  color: black;
  font-family: "Work Sans", monospace;
  font-size: 11pt;
  width: 200px;
`;

const TechStack = styled.div`
  width: 70%;
  color: grey;
`
const Links = styled.div`

`

const Position = styled.div`
  font-size: 14pt;
  font-family: "Work Sans", monospace;
  margin-bottom: -2px;
`

const Line = styled.div`
  width: 20vw;
  height: 1px;
  background-color: rgba(0,0,0,0.3);
  margin: 20px;
  margin-left: 5px;
  margin-top: 25px;
`

const Body = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 5px;
  justify-content: flex-start;
  width: 500px;
  @media (max-width: 768px) {
    width: 300px;
  }
`;

const Dates = styled.div`
  color: rgba(0, 0, 0, 0.3);
  font-family: "Work Sans", monospace;
  font-size: 11pt;
  margin: 10px;
  margin-left: 0px;
  width: 200px;
`;

const Description = styled.ul`
  margin-left: -23px;
  list-style-type: circle;
  line-height: 100%;
`;

class ExperienceCell extends Component {
  renderListCells = (description) => {
    var descArray = []
    description.forEach(function(line){
      descArray.push(<li>{line}</li>)
    })
    return descArray
  }

  render () {
    return (
      <ExpCellContainer>
        <Header>
          <Company>{this.props.data.company}</Company>
          <Dates>{this.props.data.dates}</Dates>
          <TechStackTitle>Tech Stack:</TechStackTitle>
          <TechStack>{this.renderListCells(this.props.data.stack)}</TechStack>
        </Header>
        <Body>
          <Position>{this.props.data.position}</Position>
          <Description>{this.renderListCells(this.props.data.description)}</Description>
        </Body>
      </ExpCellContainer>
    );
  }
}

export default ExperienceCell
